            ],
            innerRadiusRatio: 0.5,
            innerLabels: [
              {
                text: numFormatter.format(total),
                fontSize: 24,
                fontWeight: 'bold',
              },
              {
                text: 'Total',
                fontSize: 16,
              },
            ],
            highlightStyle: {
              item: {
                fillOpacity: 0,
                stroke: '#535455',
                strokeWidth: 1,
              },
            },
            tooltip: {
              renderer: ({ datum, calloutLabelKey, title, sectorLabelKey }) => {
                return {
                  title,
