import React, { Component } from 'react';
import { render } from 'react-dom';
import * as agCharts from 'ag-charts-community';
import { AgChartsReact } from 'ag-charts-react';
import axios from 'axios';

export default class C extends Component {
  constructor(props) {

    super(props);
    


   

    this.state = {
      result: [],
      d:[],
    };
  }

  

  componentDidMount() {
    axios.get('http://localhost:8080/expenses',{ headers: { "Authorization": localStorage.getItem("jwtToken")}})
		.then((response) => {
      this.setState({
        result: response.data
    });
  let a=[];
    const map1 = new Map();
      for( let i=0;i<this.state.result.size();i++)
      {
        map1[this.state.result[i].category]+=this.state.result[i].cost;
      }
      map1.forEach((value, key)=>{
         a.push({"label":key,"value":value})
      })
      console.log(a);

     this.setState({
      d:a
     });
  })}


   

  render() {
    return <AgChartsReact options={{
      data: this.state.d,
      series: [
        {
          type: 'pie',
          angleKey: 'value',
          calloutLabelKey: 'label',
          sectorLabelKey: 'value',
          sectorLabel: {
            color: 'white',
            fontWeight: 'bold',
          },
        },
      ],
    }} />;
  }
}
