import React, { Component } from 'react';
import { render } from 'react-dom';
import * as agCharts from 'ag-charts-community';
import { AgChartsReact } from 'ag-charts-react';

export default class C extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: {
        autoSize: true,
        data,
        title: {
          text: 'Expenses Based on Category',
          fontSize: 18,
        },
        series: [
          {
            type: 'pie',
            calloutLabelKey: 'type',
            fillOpacity: 1,
            strokeWidth: 0,
            angleKey: '2018/19',
            sectorLabelKey: '2018/19',
            calloutLabel: {
              enabled: false,
            },
            sectorLabel: {
              color: 'white',
              fontWeight: 'bold',
              formatter: ({ datum, sectorLabelKey }) => {
                const value = datum[sectorLabelKey];
                return numFormatter.format(value);
              },
            },
            title: {
              text: '',
            },
            fills: [
              '#fb7451',
              '#f4b944',
              '#57cc8b',
              '#49afda',
              '#3988dc',
              '#72508c',
              '#b499b5',
              '#b7b5ba',
            ],
            innerRadiusRatio: 0.5,
            innerLabels: [
              {
                text: numFormatter.format(total),
                fontSize: 24,
                fontWeight: 'bold',
              },
              {
                text: 'Total',
                fontSize: 16,
              },
            ],
            highlightStyle: {
              item: {
                fillOpacity: 0,
                stroke: '#535455',
                strokeWidth: 1,
              },
            },
            tooltip: {
              renderer: ({ datum, calloutLabelKey, title, sectorLabelKey }) => {
                return {
                  title,
                  content: `${datum[calloutLabelKey]}: ${numFormatter.format(
                    datum[sectorLabelKey]
                  )}`,
                };
              },
            },
          },
        ],
      },
    };
  }

  componentDidMount() {}

  render() {
    return <AgChartsReact options={this.state.options} />;
  }
}

function getData() {
    return [
      { type: 'House - single occupancy', '2018/19': 15349 },
      { type: 'Bungalow - single occupancy', '2018/19': 1656 },
      { type: 'Converted Flat/Maisonette - single occupancy', '2018/19': 2147 },
      { type: 'Purpose Built Low Rise (1-3) Flats/Maisonettes', '2018/19': 4954 },
      { type: 'Purpose Built Medium Rise (4-9) Flats', '2018/19': 1887 },
      { type: 'Purpose Built High Rise (10+) Flats', '2018/19': 820 },
      { type: 'Dwelling - multiple occupancy', '2018/19': 610 },
      { type: 'Other dwelling', '2018/19': 2147 },
    ];
  }
const data = getData();
const numFormatter = new Intl.NumberFormat('en-US');
const total = data.reduce((sum, d) => sum + d['2018/19'], 0);
