import React, { Component } from 'react';
import { render } from 'react-dom';
import * as agCharts from 'ag-charts-community';
import { AgChartsReact } from 'ag-charts-react';

export default class C extends Component {
  constructor(props) {
    super(props);

    this.state = {
      result: [],
      options: {
        autoSize: true,
        data,
        title: {
          text: 'Dwelling Fires (UK)',
          fontSize: 18,
        },
        subtitle: {
          text: 'Source: Home Office',
        },
        series: [
          {
            type: 'pie',
            calloutLabelKey: 'type',
            fillOpacity: 0.9,
            strokeWidth: 0,
            angleKey: '2018/19',
            sectorLabelKey: '2018/19',
            calloutLabel: {
              enabled: false,
            },
            sectorLabel: {
              color: 'white',
              fontWeight: 'bold',
              formatter: ({ datum, sectorLabelKey }) => {
                const value = datum[sectorLabelKey];
                return numFormatter.format(value);
              },
            },
            title: {
              text: '2018/19',
            },
            fills: [
              '#fb7451',
              '#f4b944',
              '#57cc8b',
              '#49afda',
              '#3988dc',
              '#72508c',
              '#b499b5',
              '#b7b5ba',
            ],
            innerRadiusRatio: 0.5,
            innerLabels: [
              {
                text: numFormatter.format(total),
                fontSize: 24,
                fontWeight: 'bold',
              },
              {
                text: 'Total',
                fontSize: 16,
              },
            ],
            highlightStyle: {
              item: {
                fillOpacity: 0,
                stroke: '#535455',
                strokeWidth: 1,
              },
            },
            tooltip: {
              renderer: ({ datum, calloutLabelKey, title, sectorLabelKey }) => {
                return {
                  title,
                  content: `${datum[calloutLabelKey]}: ${numFormatter.format(
                    datum[sectorLabelKey]
                  )}`,
                };
              },
            },
          },
        ],
      },
    };
  }

  componentDidMount() {
    axios.get('http://localhost:8080/expenses',{ headers: { "Authorization": localStorage.getItem("jwtToken")}})
		.then((response) => {
      this.setState({
        result: response.data
    });
			var i;
			var temp = 0;
			/* calculation for the days with multiple expenses entered */
			for (i = 0; i < response.data.length; i++) {
				if (i !== (response.data.length - 1) && !response.data[i + 1].date.localeCompare(response.data[i].date)) {
					temp += response.data[i].cost;
				} else {
					temp += response.data[i].cost;
					var day = response.data[i].date.substring(0, 10);
					this.dataPoints.push({
						date: String(day,),
						spending: Number(temp),
					});
					temp = 0;
				}
			}

			this.setState({
				options: {
					
			    	...this.state.options,
			    	data: this.dataPoints,
				},
			});
		}) 
		.catch(err => {
			this.setState({
				error: <div className="alert alert-warning" style={{ marginTop: '5%' }}>Please Login...</div>
			})
		});
		

  }

  render() {
    return <AgChartsReact options={this.state.options} />;
  }
}
