import React, { useState } from "react";
import { useDispatch } from "react-redux";
import axios from "axios";
import NavBar from "../NavBar";

import {
  Row,
  Col,
  Card,
  Form,
  InputGroup,
  FormControl,
  Button,
} from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPhone,
  faEnvelope,
  faLock,
  faUndo,
  faUserPlus,
  faUser,
  faAddressBook,
} from "@fortawesome/free-solid-svg-icons";
import MyToast from "../MyToast";

const REGISTER_URL = "http://localhost:8080/user/register";
 const USER_REQUEST = "USER_REQUEST";
 const USER_SUCCESS = "USER_SUCCESS";
 const USER_FAILURE = "USER_FAILURE";
 const USER_SAVED_SUCCESS = "USER_SAVED_SUCCESS";
const userRequest = () => {
    return {
      type: USER_REQUEST,
    };
  };
  
  const userSavedSuccess = (user) => {
    return {
      type: USER_SAVED_SUCCESS,
      payload: user,
    };
  };
  
  const userSuccess = (users) => {
    return {
      type: USER_SUCCESS,
      payload: users,
    };
  };
  
  const userFailure = (error) => {
    return {
      type: USER_FAILURE,
      payload: error,
    }};

const Register = (props) => {
  const [show, setShow] = useState(false);
  const [message, setMessage] = useState("");

  const initialState = {
    id:"1",
    name: "",
    email: "",
    password: "",
    mobile: "",
    address: "",
  };

  const [user, setUser] = useState(initialState);

  const userChange = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value, id: value });
  };
  

  const dispatch = useDispatch();

  const registerUser = async() =>  {
    console.log("ssw");
    try {
      const response = await axios.post(REGISTER_URL, user);
      localStorage.getItem("jwtToken", response.data.token);
      setShow(true);
      setMessage(response.message);
      resetRegisterForm();
      setTimeout(() => {
        setShow(false);
        props.history.push("/login")});
    } catch (error) {
        console.log(error);
    }
  };

//   const saveUser = () => {
//     dispatch(registerUser(user))
//       .then((response) => {
//         setShow(true);
//         setMessage(response.message);
//         resetRegisterForm();
//         setTimeout(() => {
//           setShow(false);
//           props.history.push("/login");
//         }, 2000);
//       })
//       .catch((error) => {
//         console.log(error);
//       });
  

  const resetRegisterForm = () => {
    setUser(initialState);
  };

  return (
    <div>
        <NavBar/>
        <br></br>
    <br></br>
    <br></br>
    <br></br>
    
      <div style={{ display: show ? "block" : "none" }}>
        <MyToast show={show} message={message} type={"success"} />
      </div>
      <Row className="justify-content-md-center">
        <Col xs={5}>
          <Card className={"border border-dark bg-dark text-white"}>
            <Card.Header>
              <FontAwesomeIcon icon={faUserPlus} /> Register
            </Card.Header>
            <Card.Body>
              <Form.Row>
                <Form.Group as={Col}>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faUser} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      autoComplete="off"
                      type="text"
                      name="name"
                      value={user.name}
                      onChange={userChange}
                      className={"bg-dark text-white"}
                      placeholder="Enter Name"
                    />
                  </InputGroup>
                </Form.Group>
              </Form.Row>
              <Form.Row>
                <Form.Group as={Col}>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faEnvelope} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      required
                      autoComplete="off"
                      type="text"
                      name="email"
                      value={user.email}
                      onChange={userChange}
                      className={"bg-dark text-white"}
                      placeholder="Enter Email Address"
                    />
                  </InputGroup>
                </Form.Group>
              </Form.Row>
              <Form.Row>
                <Form.Group as={Col}>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faLock} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      required
                      autoComplete="off"
                      type="password"
                      name="password"
                      value={user.password}
                      onChange={userChange}
                      className={"bg-dark text-white"}
                      placeholder="Enter Password"
                    />
                  </InputGroup>
                </Form.Group>
              </Form.Row>
              <Form.Row>
                <Form.Group as={Col}>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faPhone} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      autoComplete="off"
                      type="text"
                      name="mobile"
                      value={user.mobile}
                      onChange={userChange}
                      className={"bg-dark text-white"}
                      placeholder="Enter Mobile Number"
                    />
                  </InputGroup>
                  <Form.Row>
                <Form.Group as={Col}>
                  <br></br>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faAddressBook} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      autoComplete="off"
                      type="text"
                      name="address"
                      value={user.ddress}
                      onChange={userChange}
                      className={"bg-dark text-white"}
                      placeholder="Address"
                    />
                  </InputGroup>
                </Form.Group>
              </Form.Row>
                </Form.Group>
              </Form.Row>
            </Card.Body>
            <Card.Footer style={{ textAlign: "right" }}>
              <Button
                size="sm"
                type="button"
                variant="success"
                onClick={registerUser}
                disabled={user.email.length === 0 || user.password.length === 0}
              >
                <FontAwesomeIcon icon={faUserPlus} /> Register
              </Button>{" "}
              <Button
                size="sm"
                type="button"
                variant="info"
                onClick={resetRegisterForm}
              >
                <FontAwesomeIcon icon={faUndo} /> Reset
              </Button>
            </Card.Footer>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Register;
